import React from 'react';
import './Achievements.css';

const Achievements = () => {
  const achievements = [
    {
      title: "SatoshiGPT - Product Hunt #3",
      description: "SatoshiGPT ranked #3 Product of the Day on Product Hunt",
      date: "June 26, 2023",
      icon: "üèÜ",
      link: "https://www.producthunt.com/products/satoshigpt"
    },
    {
      title: "NTSE Scholar 2016",
      description: "National Talent Search Examination Scholar",
      icon: "üèÜ"
    },
    {
      title: "700+ LeetCode Problems",
      description: "Solved over 700 algorithmic problems",
      icon: "üíª"
    },
    {
      title: "Representative",
      description: "Enigma, Quizzing Club of IIT Ropar",
      icon: "üß†"
    },
    {
      title: "Project Member Enactus",
      description: "IIT Ropar (2018-20)",
      icon: "ü§ù"
    },
    {
      title: "NSS Volunteer",
      description: "National Service Scheme (2018-20)",
      icon: "üåü"
    }
  ];

  return (
    <section id="achievements" className="section achievements">
      <h2>Achievements & Activities</h2>
      <div className="achievements-grid">
        {achievements.map((achievement, index) => (
          <div key={index} className="achievement-card">
            <div className="achievement-icon">{achievement.icon}</div>
            <h3 className="achievement-title">{achievement.title}</h3>
            <p className="achievement-description">{achievement.description}</p>
            {achievement.date && (
              <div className="achievement-date">{achievement.date}</div>
            )}
            {achievement.link && (
              <a 
                href={achievement.link} 
                target="_blank" 
                rel="noopener noreferrer"
                className="achievement-link"
              >
                View on Product Hunt ‚Üí
              </a>
            )}
          </div>
        ))}
      </div>
    </section>
  );
};

export default Achievements;
