import React from 'react';
import './Achievements.css';

const Achievements = () => {
  const achievements = [
    {
      title: "SatoshiGPT - Product Hunt #3",
      description: "SatoshiGPT ranked #3 Product of the Day on Product Hunt",
      date: "June 26, 2023",
      icon: "üèÜ",
      link: "https://www.producthunt.com/products/satoshigpt",
      category: "Product Launch"
    },
    {
      title: "Full Stack Developer Certification",
      description: "Completed comprehensive full-stack development program",
      date: "2023",
      icon: "üéì",
      category: "Education"
    },
    {
      title: "React.js Expert",
      description: "Advanced proficiency in React.js and modern web development",
      date: "2023",
      icon: "‚öõÔ∏è",
      category: "Technical"
    },
    {
      title: "Open Source Contributor",
      description: "Active contributor to various open source projects",
      date: "2022-2023",
      icon: "üåü",
      category: "Community"
    }
  ];

  return (
    <section id="achievements" className="achievements">
      <div className="container">
        <h2 className="section-title">Achievements</h2>
        <p className="section-subtitle">Milestones and recognitions in my journey</p>

        <div className="achievements-grid">
          {achievements.map((achievement, index) => (
            <div key={index} className="achievement-card">
              <div className="achievement-icon">{achievement.icon}</div>
              <div className="achievement-content">
                <div className="achievement-category">{achievement.category}</div>
                <h3 className="achievement-title">{achievement.title}</h3>
                <p className="achievement-description">{achievement.description}</p>
                <div className="achievement-date">{achievement.date}</div>
                {achievement.link && (
                  <a 
                    href={achievement.link} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="achievement-link"
                  >
                    View on Product Hunt ‚Üí
                  </a>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Achievements;
