import React, { useRef, useEffect, useState } from 'react';
import './ParticleNetwork.css';

const ParticleNetwork = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);
  const mouseRef = useRef({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    let canvasWidth, canvasHeight;

    // Set canvas size
    const resizeCanvas = () => {
      const rect = canvas.getBoundingClientRect();
      canvasWidth = rect.width;
      canvasHeight = rect.height;

      // Set actual canvas size in memory (scaled for high DPI)
      canvas.width = canvasWidth * window.devicePixelRatio;
      canvas.height = canvasHeight * window.devicePixelRatio;

      // Scale the drawing context so everything draws at the correct size
      ctx.scale(window.devicePixelRatio, window.devicePixelRatio);

      // Set the CSS size to match the container
      canvas.style.width = '100%';
      canvas.style.height = '100%';
    };

    // Initial resize
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // Particle system
    const particles = [];
    const particleCount = 60;
    const maxDistance = 150;
    const mouseRadius = 120;

    class Particle {
      constructor() {
        this.reset();
        this.hue = Math.random() * 60 + 200; // Blue to purple range
      }

      reset() {
        this.x = Math.random() * canvasWidth;
        this.y = Math.random() * canvasHeight;
        this.vx = (Math.random() - 0.5) * 0.8;
        this.vy = (Math.random() - 0.5) * 0.8;
        this.radius = Math.random() * 3 + 1;
        this.originalRadius = this.radius;
        this.opacity = Math.random() * 0.6 + 0.4;
      }

      update() {
        this.x += this.vx;
        this.y += this.vy;

        // Bounce off edges with some randomness
        if (this.x < 0 || this.x > canvasWidth) {
          this.vx *= -0.8;
          this.vx += (Math.random() - 0.5) * 0.2;
        }
        if (this.y < 0 || this.y > canvasHeight) {
          this.vy *= -0.8;
          this.vy += (Math.random() - 0.5) * 0.2;
        }

        // Keep particles in bounds
        this.x = Math.max(0, Math.min(canvasWidth, this.x));
        this.y = Math.max(0, Math.min(canvasHeight, this.y));

        // Mouse interaction
        const dx = mouseRef.current.x - this.x;
        const dy = mouseRef.current.y - this.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < mouseRadius && isHovering) {
          // Attract to mouse with varying force
          const force = (mouseRadius - distance) / mouseRadius;
          this.vx += (dx / distance) * force * 0.02;
          this.vy += (dy / distance) * force * 0.02;
          this.radius = this.originalRadius * (1 + force * 0.5);
          this.opacity = Math.min(1, this.opacity + force * 0.3);
        } else {
          this.radius = this.originalRadius;
          this.opacity = Math.max(0.4, this.opacity - 0.01);
        }

        // Add some drift
        this.vx += (Math.random() - 0.5) * 0.01;
        this.vy += (Math.random() - 0.5) * 0.01;

        // Damping
        this.vx *= 0.995;
        this.vy *= 0.995;
      }

      draw() {
        // Main particle
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.radius, 0, Math.PI * 2);
        ctx.fillStyle = `hsla(${this.hue}, 70%, 60%, ${this.opacity})`;
        ctx.fill();

        // Glow effect
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.radius * 2, 0, Math.PI * 2);
        ctx.fillStyle = `hsla(${this.hue}, 70%, 60%, ${this.opacity * 0.1})`;
        ctx.fill();
      }
    }

    // Initialize particles after canvas is sized
    const initParticles = () => {
      particles.length = 0; // Clear existing particles
      for (let i = 0; i < particleCount; i++) {
        particles.push(new Particle());
      }
    };

    // Animation loop
    const animate = () => {
      if (!canvasWidth || !canvasHeight) return;

      // Create trailing effect
      ctx.fillStyle = 'rgba(0, 0, 0, 0.05)';
      ctx.fillRect(0, 0, canvasWidth, canvasHeight);

      // Update and draw particles
      particles.forEach(particle => {
        particle.update();
        particle.draw();
      });

      // Draw connections
      for (let i = 0; i < particles.length; i++) {
        for (let j = i + 1; j < particles.length; j++) {
          const dx = particles[i].x - particles[j].x;
          const dy = particles[i].y - particles[j].y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < maxDistance) {
            const opacity = (1 - distance / maxDistance) * 0.4;
            const gradient = ctx.createLinearGradient(
              particles[i].x, particles[i].y,
              particles[j].x, particles[j].y
            );
            gradient.addColorStop(0, `hsla(${particles[i].hue}, 70%, 60%, ${opacity})`);
            gradient.addColorStop(1, `hsla(${particles[j].hue}, 70%, 60%, ${opacity})`);

            ctx.beginPath();
            ctx.moveTo(particles[i].x, particles[i].y);
            ctx.lineTo(particles[j].x, particles[j].y);
            ctx.strokeStyle = gradient;
            ctx.lineWidth = 1;
            ctx.stroke();
          }
        }
      }

      // Draw mouse connections
      if (isHovering) {
        particles.forEach(particle => {
          const dx = mouseRef.current.x - particle.x;
          const dy = mouseRef.current.y - particle.y;
          const distance = Math.sqrt(dx * dx + dy * dy);

          if (distance < mouseRadius) {
            const opacity = (1 - distance / mouseRadius) * 0.6;
            ctx.beginPath();
            ctx.moveTo(particle.x, particle.y);
            ctx.lineTo(mouseRef.current.x, mouseRef.current.y);
            ctx.strokeStyle = `hsla(280, 70%, 60%, ${opacity})`;
            ctx.lineWidth = 2;
            ctx.stroke();
          }
        });

        // Draw mouse node with pulsing effect
        const time = Date.now() * 0.005;
        const pulseRadius = 8 + Math.sin(time) * 3;
        ctx.beginPath();
        ctx.arc(mouseRef.current.x, mouseRef.current.y, pulseRadius, 0, Math.PI * 2);
        ctx.fillStyle = 'hsla(280, 70%, 60%, 0.8)';
        ctx.fill();

        // Outer glow
        ctx.beginPath();
        ctx.arc(mouseRef.current.x, mouseRef.current.y, pulseRadius * 2, 0, Math.PI * 2);
        ctx.fillStyle = 'hsla(280, 70%, 60%, 0.2)';
        ctx.fill();
      }

      animationRef.current = requestAnimationFrame(animate);
    };

    // Start animation with proper initialization
    const startAnimation = () => {
      resizeCanvas();
      initParticles();
      animate();
    };

    // Use a timeout to ensure the canvas is properly mounted
    const timeoutId = setTimeout(startAnimation, 50);

    // Mouse event handlers
    const handleMouseMove = (e) => {
      const rect = canvas.getBoundingClientRect();
      mouseRef.current.x = e.clientX - rect.left;
      mouseRef.current.y = e.clientY - rect.top;
    };

    const handleMouseEnter = () => setIsHovering(true);
    const handleMouseLeave = () => setIsHovering(false);

    canvas.addEventListener('mousemove', handleMouseMove);
    canvas.addEventListener('mouseenter', handleMouseEnter);
    canvas.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      clearTimeout(timeoutId);
      window.removeEventListener('resize', resizeCanvas);
      canvas.removeEventListener('mousemove', handleMouseMove);
      canvas.removeEventListener('mouseenter', handleMouseEnter);
      canvas.removeEventListener('mouseleave', handleMouseLeave);
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [isHovering]);

  return (
    <div className="particle-network">
      <canvas ref={canvasRef} className="particle-canvas" />
    </div>
  );
};

export default ParticleNetwork;
